package listaexercicios2;

public class Ex10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double i = 1;
		double x, resultado = 0;

		while(i<=20){
		x=1/i;
		resultado = resultado +x;
		i++;
		}
		
		System.out.println("O resultado é: " + resultado);
	}

}
